import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchBy, setSearchBy] = useState('name'); // 'name', 'ingredient', 'cuisine'
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  
  // State for ingredient-based search
  const [ingredients, setIngredients] = useState(['']);
  // TheMealDB doesn't require an API key for the free tier

  // For demo purposes, we'll use a mock API call
  const searchRecipes = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // In a real app, you would use an actual API like:
      // const response = await axios.get(`https://api.spoonacular.com/recipes/complexSearch?query=${searchTerm}&apiKey=YOUR_API_KEY`);
      
      // For demo, we'll simulate an API response
      setTimeout(() => {
        const mockRecipes = [
          {
            id: 1,
            title: 'Spaghetti Carbonara',
            image: 'https://images.unsplash.com/photo-1612874742237-6526221588e3?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1771&q=80',
            readyInMinutes: 30,
            servings: 4,
            cuisines: ['Italian'],
          },
          {
            id: 2,
            title: 'Chicken Tikka Masala',
            image: 'https://images.unsplash.com/photo-1565557623262-b51c2513a641?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1771&q=80',
            readyInMinutes: 45,
            servings: 6,
            cuisines: ['Indian'],
          },
          {
            id: 3,
            title: 'Beef Tacos',
            image: 'https://images.unsplash.com/photo-1551504734-5ee1c4a1479b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1770&q=80',
            readyInMinutes: 25,
            servings: 4,
            cuisines: ['Mexican'],
          },
          {
            id: 4,
            title: 'Vegetable Stir Fry',
            image: 'https://images.unsplash.com/photo-1512621776951-a57141f2eefd?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1770&q=80',
            readyInMinutes: 20,
            servings: 2,
            cuisines: ['Asian', 'Chinese'],
          },
        ];

        // Filter based on search term (case insensitive)
        const filteredRecipes = mockRecipes.filter(recipe => {
          const term = searchTerm.toLowerCase();
          if (searchBy === 'name') {
            return recipe.title.toLowerCase().includes(term);
          } else if (searchBy === 'cuisine') {
            return recipe.cuisines.some(cuisine => cuisine.toLowerCase().includes(term));
          }
          // For ingredients, we would need ingredient data in a real app
          return true;
        });

        setRecipes(filteredRecipes);
        setLoading(false);
      }, 1000); // Simulate network delay

    } catch (err) {
      setError('Failed to fetch recipes. Please try again.');
      setLoading(false);
      console.error('Error fetching recipes:', err);
    }
  };

  // New function to search recipes by ingredients using Spoonacular API
  const searchByIngredients = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    // Filter out empty ingredients
    const filteredIngredients = ingredients.filter(ing => ing.trim() !== '');
    
    if (filteredIngredients.length === 0) {
      setError('Please enter at least one ingredient');
      setLoading(false);
      return;
    }

    try {
      // For a real implementation, uncomment this code and add your API key
      if (!apiKey) {
        // For demo purposes, we'll use mock data if no API key is provided
        setTimeout(() => {
          const mockRecipes = [
            {
              id: 1,
              title: 'Pasta with Garlic and Olive Oil',
              image: 'https://images.unsplash.com/photo-1621996346565-e3dbc646d9a9?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=880&q=80',
              usedIngredientCount: 2,
              missedIngredientCount: 0,
              likes: 209,
              readyInMinutes: 20,
              servings: 2,
              cuisines: ['Italian'],
            },
            {
              id: 2,
              title: 'Garlic Butter Shrimp',
              image: 'https://images.unsplash.com/photo-1625944525533-473f1a3d54e7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=880&q=80',
              usedIngredientCount: 1,
              missedIngredientCount: 2,
              likes: 180,
              readyInMinutes: 15,
              servings: 4,
              cuisines: ['American', 'Seafood'],
            },
          ];
          
          setRecipes(mockRecipes);
          setLoading(false);
        }, 1000);
        return;
      }
      
      // Real API call to Spoonacular
      const ingredientString = filteredIngredients.join(',');
      const response = await axios.get(
        `https://api.spoonacular.com/recipes/findByIngredients`,
        {
          params: {
            ingredients: ingredientString,
            number: 10, // Number of results to return
            ranking: 1, // 1 = maximize used ingredients, 2 = minimize missing ingredients
            ignorePantry: true, // Ignore typical pantry items like salt, oil, etc.
            apiKey: apiKey
          }
        }
      );
      
      // Transform the response to match our recipe card format
      const formattedRecipes = response.data.map(recipe => ({
        id: recipe.id,
        title: recipe.title,
        image: recipe.image,
        readyInMinutes: recipe.readyInMinutes || 30, // Default value if not provided
        servings: recipe.servings || 4, // Default value if not provided
        cuisines: [], // API doesn't return cuisines in this endpoint
        usedIngredientCount: recipe.usedIngredientCount,
        missedIngredientCount: recipe.missedIngredientCount,
        likes: recipe.likes
      }));
      
      setRecipes(formattedRecipes);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch recipes. Please check your API key or try again later.');
      setLoading(false);
      console.error('Error fetching recipes by ingredients:', err);
    }
  };

  // Handle adding a new ingredient input field
  const addIngredientField = () => {
    setIngredients([...ingredients, '']);
  };

  // Handle removing an ingredient input field
  const removeIngredientField = (index) => {
    const newIngredients = [...ingredients];
    newIngredients.splice(index, 1);
    setIngredients(newIngredients);
  };

  // Handle ingredient input change
  const handleIngredientChange = (index, value) => {
    const newIngredients = [...ingredients];
    newIngredients[index] = value;
    setIngredients(newIngredients);
  };

  // Handle API key input change
  const handleApiKeyChange = (e) => {
    setApiKey(e.target.value);
  };

  return (
    <div className="home-page">
      <div className="hero">
        <h1>Find Your Perfect Recipe</h1>
        <p>Search thousands of recipes by name, ingredients, or cuisine</p>
        
        {/* Original search form */}
        <form onSubmit={searchRecipes} className="search-form">
          <div className="search-options">
            <label>
              <input 
                type="radio" 
                name="searchBy" 
                value="name" 
                checked={searchBy === 'name'}
                onChange={() => setSearchBy('name')} 
              />
              Recipe Name
            </label>
            <label>
              <input 
                type="radio" 
                name="searchBy" 
                value="ingredient" 
                checked={searchBy === 'ingredient'}
                onChange={() => setSearchBy('ingredient')} 
              />
              Ingredient
            </label>
            <label>
              <input 
                type="radio" 
                name="searchBy" 
                value="cuisine" 
                checked={searchBy === 'cuisine'}
                onChange={() => setSearchBy('cuisine')} 
              />
              Cuisine
            </label>
          </div>
          
          <div className="search-input-container">
            <input
              type="text"
              placeholder={`Search by ${searchBy}...`}
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              required
              className="search-input"
            />
            <button type="submit" className="search-button">
              <i className="fas fa-search"></i> Search
            </button>
          </div>
        </form>

        {/* New ingredient-based search form */}
        <div className="ingredient-search-section">
          <h2>Or Find Recipes By Available Ingredients</h2>
          <form onSubmit={searchByIngredients} className="ingredient-search-form">
            <div className="api-key-input">
              <input
                type="text"
                placeholder="Enter your Spoonacular API Key (optional for demo)"
                value={apiKey}
                onChange={handleApiKeyChange}
                className="api-key-field"
              />
              <small>Get a free API key at <a href="https://spoonacular.com/food-api" target="_blank" rel="noopener noreferrer">spoonacular.com</a></small>
            </div>
            
            <div className="ingredients-container">
              {ingredients.map((ingredient, index) => (
                <div key={index} className="ingredient-input-group">
                  <input
                    type="text"
                    placeholder={`Ingredient ${index + 1} (e.g., apple, flour, chicken)`}
                    value={ingredient}
                    onChange={(e) => handleIngredientChange(index, e.target.value)}
                    className="ingredient-input"
                  />
                  {ingredients.length > 1 && (
                    <button 
                      type="button" 
                      onClick={() => removeIngredientField(index)}
                      className="remove-ingredient-btn"
                    >
                      <i className="fas fa-times"></i>
                    </button>
                  )}
                </div>
              ))}
              <button 
                type="button" 
                onClick={addIngredientField}
                className="add-ingredient-btn"
              >
                <i className="fas fa-plus"></i> Add Ingredient
              </button>
            </div>
            
            <button type="submit" className="search-button ingredient-search-btn">
              <i className="fas fa-utensils"></i> Find Recipes
            </button>
          </form>
        </div>
      </div>

      <div className="search-results">
        {loading ? (
          <div className="loading">
            <i className="fas fa-spinner fa-spin"></i> Loading recipes...
          </div>
        ) : error ? (
          <div className="error">{error}</div>
        ) : recipes.length > 0 ? (
          <>
            <h2>Search Results</h2>
            <div className="recipe-grid">
              {recipes.map((recipe) => (
                <div key={recipe.id} className="card recipe-card">
                  <img src={recipe.image} alt={recipe.title} />
                  <h3>{recipe.title}</h3>
                  <div className="recipe-info">
                    <span><i className="far fa-clock"></i> {recipe.readyInMinutes} mins</span>
                    <span><i className="fas fa-utensils"></i> {recipe.servings} servings</span>
                  </div>
                  {recipe.usedIngredientCount !== undefined && (
                    <div className="ingredient-match-info">
                      <span className="used-ingredients">
                        <i className="fas fa-check"></i> {recipe.usedIngredientCount} ingredients used
                      </span>
                      <span className="missing-ingredients">
                        <i className="fas fa-times"></i> {recipe.missedIngredientCount} ingredients missing
                      </span>
                    </div>
                  )}
                  {recipe.cuisines && recipe.cuisines.length > 0 && (
                    <div className="recipe-cuisines">
                      {recipe.cuisines.map((cuisine, index) => (
                        <span key={index} className="cuisine-tag">{cuisine}</span>
                      ))}
                    </div>
                  )}
                  <Link to={`/recipe/${recipe.id}`} className="btn">
                    View Recipe
                  </Link>
                </div>
              ))}
            </div>
          </>
        ) : searchTerm || ingredients.some(ing => ing.trim() !== '') ? (
          <div className="no-results">
            <i className="fas fa-search"></i>
            <p>No recipes found. Try different ingredients or search terms.</p>
          </div>
        ) : null}
      </div>
    </div>
  );
};

export default Home;
