import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AddRecipe.css';

const AddRecipe = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    readyInMinutes: '',
    servings: '',
    cuisines: '',
    summary: '',
    instructions: '',
    image: '',
  });

  const [ingredients, setIngredients] = useState([
    { name: '', amount: '', unit: '' },
  ]);

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleIngredientChange = (index, field, value) => {
    const updatedIngredients = [...ingredients];
    updatedIngredients[index][field] = value;
    setIngredients(updatedIngredients);
  };

  const addIngredient = () => {
    setIngredients([...ingredients, { name: '', amount: '', unit: '' }]);
  };

  const removeIngredient = (index) => {
    if (ingredients.length > 1) {
      const updatedIngredients = [...ingredients];
      updatedIngredients.splice(index, 1);
      setIngredients(updatedIngredients);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // In a real app, you would save this to local storage or a database
    // For demo purposes, we'll just show a success message
    setSubmitted(true);

    // Reset form after 3 seconds and redirect to My Recipes
    setTimeout(() => {
      setFormData({
        title: '',
        readyInMinutes: '',
        servings: '',
        cuisines: '',
        summary: '',
        instructions: '',
        image: '',
      });
      setIngredients([{ name: '', amount: '', unit: '' }]);
      setSubmitted(false);
      navigate('/my-recipes');
    }, 3000);
  };

  return (
    <div className="add-recipe-page">
      <div className="page-header">
        <h1>Add New Recipe</h1>
        <p>Share your culinary creations with the world</p>
      </div>

      {submitted ? (
        <div className="success-container">
          <div className="success-message">
            <i className="fas fa-check-circle"></i>
            <h2>Recipe Added Successfully!</h2>
            <p>Your recipe has been added to your collection.</p>
          </div>
        </div>
      ) : (
        <form className="recipe-form" onSubmit={handleSubmit}>
          <div className="form-grid">
            <div className="form-left">
              <div className="form-control">
                <label htmlFor="title">Recipe Title*</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  required
                  placeholder="e.g. Homemade Chocolate Chip Cookies"
                />
              </div>

              <div className="form-row">
                <div className="form-control">
                  <label htmlFor="readyInMinutes">Cooking Time (minutes)*</label>
                  <input
                    type="number"
                    id="readyInMinutes"
                    name="readyInMinutes"
                    value={formData.readyInMinutes}
                    onChange={handleChange}
                    required
                    min="1"
                    placeholder="e.g. 30"
                  />
                </div>

                <div className="form-control">
                  <label htmlFor="servings">Servings*</label>
                  <input
                    type="number"
                    id="servings"
                    name="servings"
                    value={formData.servings}
                    onChange={handleChange}
                    required
                    min="1"
                    placeholder="e.g. 4"
                  />
                </div>
              </div>

              <div className="form-control">
                <label htmlFor="cuisines">Cuisine(s)</label>
                <input
                  type="text"
                  id="cuisines"
                  name="cuisines"
                  value={formData.cuisines}
                  onChange={handleChange}
                  placeholder="e.g. Italian, Mediterranean (separate with commas)"
                />
              </div>

              <div className="form-control">
                <label htmlFor="image">Image URL</label>
                <input
                  type="url"
                  id="image"
                  name="image"
                  value={formData.image}
                  onChange={handleChange}
                  placeholder="https://example.com/image.jpg"
                />
              </div>

              <div className="form-control">
                <label htmlFor="summary">Recipe Summary*</label>
                <textarea
                  id="summary"
                  name="summary"
                  value={formData.summary}
                  onChange={handleChange}
                  required
                  placeholder="Brief description of your recipe"
                  rows="3"
                ></textarea>
              </div>
            </div>

            <div className="form-right">
              <div className="form-control">
                <label>Ingredients*</label>
                <div className="ingredients-container">
                  {ingredients.map((ingredient, index) => (
                    <div key={index} className="ingredient-row">
                      <input
                        type="text"
                        placeholder="Amount"
                        value={ingredient.amount}
                        onChange={(e) => handleIngredientChange(index, 'amount', e.target.value)}
                        className="ingredient-amount"
                      />
                      <input
                        type="text"
                        placeholder="Unit"
                        value={ingredient.unit}
                        onChange={(e) => handleIngredientChange(index, 'unit', e.target.value)}
                        className="ingredient-unit"
                      />
                      <input
                        type="text"
                        placeholder="Ingredient name"
                        value={ingredient.name}
                        onChange={(e) => handleIngredientChange(index, 'name', e.target.value)}
                        className="ingredient-name"
                        required
                      />
                      <button
                        type="button"
                        className="remove-ingredient-btn"
                        onClick={() => removeIngredient(index)}
                        disabled={ingredients.length === 1}
                      >
                        <i className="fas fa-times"></i>
                      </button>
                    </div>
                  ))}
                  <button
                    type="button"
                    className="add-ingredient-btn"
                    onClick={addIngredient}
                  >
                    <i className="fas fa-plus"></i> Add Ingredient
                  </button>
                </div>
              </div>

              <div className="form-control">
                <label htmlFor="instructions">Cooking Instructions*</label>
                <textarea
                  id="instructions"
                  name="instructions"
                  value={formData.instructions}
                  onChange={handleChange}
                  required
                  placeholder="Step-by-step instructions for preparing the recipe"
                  rows="10"
                ></textarea>
              </div>
            </div>
          </div>

          <div className="form-actions">
            <button type="button" className="btn btn-secondary" onClick={() => navigate('/my-recipes')}>
              Cancel
            </button>
            <button type="submit" className="btn">
              Save Recipe
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default AddRecipe;
